mapper.select:
SELECT C.CATEGORY_NO, C.CATEGORY_NAME, CL.COURSE_NO, CL.COURSE_NAME, CL.COURSE_TEACHER, CL.COURSE_COST, CL.COURSE_CURRENT_PEOPLE, CL.COURSE_MAX_PEOPLE, CL.COURSE_AGE_LIMIT, CL.COURSE_START_DATE, CL.COURSE_END_DATE, CL.COURSE_START_TIME, CL.COURSE_END_TIME, CL.COURSE_REC_START, CL.COURSE_REC_END, CL.COURSE_INFO, CL.COURSE_MAIN_IMAGE FROM CATEGORY C INNER JOIN COURSE_LIST CL ON C.CATEGORY_NO = CL.CATEGORY_NO
AND CL.COURSE_AGE_LIMIT = #{courseAgeLimit}
AND C.CATEGORY_NO = #{categoryNo}

SELECT COURSE_NO AS courseNo,COURSE_DAY_OF_THE_WEEK AS "courseDayOfTheWeek"
FROM COURSE_LIST_DAY
WHERE COURSE_NO = #{courseNo}
